package com.itonemm.myapplication;

import androidx.appcompat.app.AppCompatActivity;

import android.media.midi.MidiOutputPort;
import android.os.Bundle;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.BaseAdapter;
import android.widget.ImageView;
import android.widget.ListView;
import android.widget.TextView;

import com.bumptech.glide.Glide;

import java.util.ArrayList;

public class ListViewTestActivity extends AppCompatActivity {

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_list_view_test);

        Movie movie1=new Movie("Home","data:image/jpeg;base64,");
        Movie movie2=new Movie("Angry Bird","https://image.tmdb.org/t/p/w185/ebe8hJRCwdflNQbUjRrfmqtUiNi.jpg");
        Movie movie3=new Movie("Spider Man","https://channelmyanmar.org/wp-content/uploads/2019/07/MV5BNDE1ODBiZjctOTk3Yy00OWMzLWJiZjktMWE5MjkyN2JlYWQ2XkEyXkFqcGdeQXVyNjg2NjQwMDQ@._V1_SY1000_CR006741000_AL_.jpg");
        ArrayList<Movie> arrayList=new ArrayList<Movie>();
        arrayList.add(movie1);
        arrayList.add(movie2);
        arrayList.add(movie3);
        ListView listView=findViewById(R.id.listview);
        MovieAdapter myadapter=new MovieAdapter(arrayList);
        listView.setAdapter(myadapter);

    }


    private class MovieAdapter extends BaseAdapter{

        ArrayList<Movie> arrayList=new ArrayList<Movie>();

        public MovieAdapter(ArrayList<Movie> arrayList) {
            this.arrayList = arrayList;
        }

        @Override
        public int getCount() {
            return arrayList.size();
        }

        @Override
        public Object getItem(int position) {
            return arrayList.get(position);
        }

        @Override
        public long getItemId(int position) {
            return position;
        }

        @Override
        public View getView(int position, View convertView, ViewGroup parent) {
            LayoutInflater myinflater=getLayoutInflater();
            View view=myinflater.inflate(R.layout.movieitem,null);
            TextView txtsr=view.findViewById(R.id.txtsr);
            txtsr.setText((position+1)+"");
            TextView txtmoviename=view.findViewById(R.id.txtname);
            Movie m=arrayList.get(position);
            txtmoviename.setText(m.Name);
            ImageView imageView=view.findViewById(R.id.imageView);
            Glide.with(getApplicationContext())
                    .load(m.ImageLink)
                    .into(imageView);

            return view;
        }
    }
}
